import React, { useContext } from 'react'
import { AppContext } from '../App'

function Key({keyVal, keyType}) {
  const {
    board,
    setBoard,
    currAttempt,
    setCurrAttempt
  } = useContext(AppContext)
  function handleKeyBoard() {
    let attemptPosition = currAttempt.attempt;
    let letterPosition = currAttempt.letter;

    if(keyType === 'Tuige') {
      if(letterPosition === 0) return;
      let newBoard = [...board];
      newBoard[attemptPosition][letterPosition - 1] =''
      setBoard(newBoard)
      setCurrAttempt({
        ...currAttempt,
        letter: letterPosition - 1
      })
    }
    if(keyType === 'normal') {
      if(letterPosition === 5) return;
      let newBoard = [...board];
      newBoard[attemptPosition][letterPosition] = keyVal
      setBoard(newBoard)
      setCurrAttempt({
        ...currAttempt,
        letter: letterPosition + 1
      })
    }
    if(keyType === 'Enter') {
      if(letterPosition !== 5) return;
      setCurrAttempt({
        attempt: attemptPosition + 1,
        letter: 0
      })
    }
  }
  return (
    <div 
      className='key'
      onClick={handleKeyBoard}
    >
      {keyVal}
    </div>
  )
}

export default Key
