import { AppContext } from '../App'
import { boardDefault } from '../Word'

function Restart() {
    const {
        setGameOver, 
        setCurrAttempt,
        setBoard
    } = useContext(AppContext)

    function restartGame() {
        setGameOver({
            gameOver: false,
            gussed: false
        })
        setCurrAttempt({
            attempt: 0,
            letter: 0
        })
        setBoard(boardDefault)
    }
    return (
        <div 
            className='zailaiyiju'
            onClick={restartGame}
        >
            再来一局
        </div>
    )
}

export default Restart
