import React, { useContext } from 'react'
import { AppContext } from '../App'

function Key({keyVal, keyType}) {
  const {
    board,
    setBoard,
    currAttempt,
    setCurrAttempt,
    onDelete,
    onEnter,
    onInput
  } = useContext(AppContext)
  function handleKeyBoard() {
    if(keyType === 'Tuige') {
      onDelete()
    }
    if(keyType === 'normal') {
      onEnter()
    }
    if(keyType === 'Enter') {
      onInput(keyVal)
    }
  }
  return (
    <div 
      className='key'
      onClick={handleKeyBoard}
    >
      {keyVal}
    </div>
  )
}

export default Key
