import React, { useState } from 'react'

function Tips() {
  const [tishikai,setTishi] = useState(false)
  function tishi() {
    setTishi(true)
  }

  return (
    <>
      <div className='gameopt'>
        <div 
          className='restart huanci'
          onClick={tishi}
        >
          &nbsp;给点提示&nbsp;
        </div>
        <div 
          className='restart toukan'
        >
          &nbsp;偷看答案&nbsp;
        </div>
      </div>
      <div className={tishikai? `tishikai`:`tishiguan`}>
        这个单词的字母以a开头，以j结尾
      </div>
    </>

  )
}

export default Tips
