import React, { useContext } from 'react'
import { AppContext } from '../App'

function Key({keyVal, keyType}) {
  const {
    onDelete,
    onEnter,
    onInput,
    disabledWord
  } = useContext(AppContext)
  let disabled = false;

  if(disabledWord.include(keyVal)) {
    disabled = true;
  }
  function handleKeyBoard() {
    if(keyType === 'Tuige') {
      onDelete()
    }
    if(keyType === 'normal') {
      onInput(keyVal)
    }
    if(keyType === 'Enter') {
      onEnter()
    }
  }
  return (
    <div 
      className={`key ${disabled? 'disabled':''}`}
      onClick={handleKeyBoard}
    >
      {keyVal}
    </div>
  )
}

export default Key
